import './App.css';
import React, { useState } from 'react';
import axios from 'axios';

function App() {
  // string
  const [pokemonName, setPokemonName] = useState("");
  // object
  const [pokemon, setPokemon] = useState({});

  // search function for the fetch 
  const searchPokemon = () =>{
    // import the data using hook to the api in axios
    axios
    .get(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`).then((res)=>{
      setPokemon({
        name: pokemonName,
        species: res.data.species.name,
        img: res.data.sprites.front_default,
        hp: res.data.stats[0].base_stat,
        attack: res.data.stats[1].base_stat,
        defense: res.data.stats[2].base_stat,
        type: res.data.types[0].base_stat,
      });
    })
  }
  return (
    <div className="App">
      <div className="TitleSection">
      <h1>Pokemon Stats</h1>
      <input type="text"
       placeholder='Pokemon'
       onChange={(event)=>{
        // get the value of the input using useState hook
        setPokemonName(event.target.value);
       }}
       />
      <button onClick={searchPokemon}>Search</button>
      </div>
    </div>
  );
}

export default App;
