import './App.css';
import {useStaste} from 'react';

function App() {
  const [pokemonName, setPokemonName] = useStaste("");
  return (
    <>
    <div className="App">
      <div className="TitleSection">
      <h1>Pokemon Stats</h1>
      <input type="text"
       placeholder='Pokemon'
       onChange={(event)=>{
        // get the value of the input using useState hook
        setPokemonName(event.target.value);
       }}
       />
      <button>Search</button>
      </div>
      <h1>{pokemonName}</h1>
    </div>
    </>
  );
}

export default App;
