import './App.css';
import React, { useState } from 'react';
import axios from 'axios';

function App() {
  // string
  const [pokemonName, setPokemonName] = useState("");
  // boolean
  const [chosen, setChosen] = useState(false);
  // object
  const [pokemon, setPokemon] = useState({
    name: "",
    species: "",
    img: "",
    hp: "",
    attack: "",
    defense: "",
    type: "",
  });
  const [empoy, setEmpoy] = useState('');
  document.addEventListener("keydown", ((e) => {
    if(e.key === "Enter") {
      searchPokemon()
    }

}))
  // search function for the fetch 
  const searchPokemon = () =>{
    // import the data using hook to the api in axios
    axios
    .get(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`).then((res)=>{
      console.log(res)
    })
  }
  const headers = {
    headers: {
      'authorization': 'Basic aW5wbGF5OjEyMzQ1Njc4'
    }
  }
  axios
    .get('https://aps.inplay.ph:3006/api/inplay/feeds/rtg?key=cbc9b305ba704448be45ee840e56c8f4',headers).then((res)=>{
      // console.log(res.data.results.root.data[1].gameName)
      setEmpoy(res.data.results.root.data.gameName)
    })
  return (
    <div className="App">
      <div className="TitleSection">
      <h1>Pokemon Stats</h1>
      <input type="text"
       placeholder='Pokemon'
       onChange={(event)=>{
        // get the value of the input using useState hook
        setPokemonName(event.target.value);
       }}
       />
      <button onClick={searchPokemon}>Search</button>
      <h1>{empoy}</h1>
      {
        empoy.map((data, index) => {
          <>
          <div key={index}>
          <h6>{data}</h6>
          </div>
          </>
      })
      }
      </div>
      <div className="DisplaySection">
        {/* if the pokemon hasnt chosen yet please choose */}
       {!chosen ? (<h1>Please choose a Pokemon</h1>) : (
       <>
       <h1>{pokemon.name}</h1>
       <img src={pokemon.img} alt="" />
       <h3>Species: {pokemon.species}</h3>
       <h4>Health: {pokemon.hp}</h4>
       <h4>Attack: {pokemon.attack}</h4>
       <h4>Defense: {pokemon.defense}</h4>
       <h4>Type: {pokemon.type}</h4>
       </>

       )}
      </div>
    </div>
  );
}

export default App;
